mapper.select:
SELECT COUNT(*)
FROM tbl_user_class
WHERE class_no = #{classNo}
AND user_id = #{userId}

mapper.update:
UPDATE tbl_user_class
SET graduate_code = #{graduateCode}
WHERE user_id = #{userId}
AND class_no = #{classNo}

mapper.select:
SELECT
    graduate_code
FROM tbl_user_class
WHERE class_no = #{classNo}
  AND user_id = #{userId}
